mapper.select:
   select * from
	(	
		select
			p.* ,
			to_number(floor((sysdate-p_date)*24*60*60)) as p_time,
			rank() over( order by p_idx  ) no
			from (select * from product
			where c_idx=#{ c_idx }
			) p 
	)	
	where no between #{ start } and #{ end }


select * from 
	(
		select p.*,
		to_number(floor((sysdate-p_date)*24*60*60)) as p_time,
		rank() over( order by p_idx  ) no
		from(select * from product
		where p_name like '%' || #{ p_name } || '%'
		) p
	)
where no between #{ start } and ${ end }

select * from
	(
		select p.*,
	  	to_number(floor((sysdate-p_date)*24*60*60)) as p_time,
	  	rank() over( order by p_idx  ) no
	  	from(select * from product
	  	where p_price between #{ min_p } and #{ max_p }

	  	)p
	)
where no between #{ start } and #{ end }
